package com.leetcode;

import java.util.HashMap;
import java.util.HashSet;

public class Leetcode389 {
    public static void main(String[] args) {

        Leetcode389 cl = new Leetcode389();

        String a = "";
        String b = "";

        cl.findTheDifference(a,b);

    }
    public char findTheDifference(String s, String t) {

        HashMap<Character, Integer> hashMap = new HashMap<>();

        for(int i = 0; i < s.length(); i++){

            Integer val = (Integer) hashMap.putIfAbsent(s.charAt(i), 1);

            if(val != null){

                hashMap.put(s.charAt(i), val + 1);
            }
        }

        for(int i = 0; i < t.length(); i++){

            Integer curr = (Integer) hashMap.get(t.charAt(i));

            if(curr == null || curr == 0){

                return t.charAt(i);

            } else {

                hashMap.put(t.charAt(i), curr - 1);

            }
        }

        return t.charAt(0);
    }
}
